import './App.css';
import React, {Component} from 'react';
import HeaderSection from './components/HeaderSection';
import PersonalInfo from './components/PersonalInfo';
import uniqid from "uniqid";

class App extends Component {
  constructor() {
    super();

    this.state = {
      personalInfo: {
        firstName: '',
        id: uniqid()
      }
    };
  }

  handleChange = (e) => {
    this.setState({
      personalInfo : {
        firstName: e.target.value,
        id: this.state.personalInfo.id,
      }
    });
  };

  onSubmitPersonalInfo = (e) => {
    e.preventDefault();
    this.setState({
      personalInfos: this.state.personalInfos.concat(this.state.firstName),
      personalInfo: {
        firstName: ''
      },
    });
  };

  render(){
    const {personalInfo, personalInfos } = this.state;
    return (
      <div className='App'>
        <HeaderSection />
        <form onSubmit={this.onSubmitPersonalInfo}>
          <label htmlFor="firstNameInput" />
          <input 
            onChange={this.handleChange}
            value={ personalInfo.firstName }
            type='text'
            id='firstNameInput'
            placeholder='First name' 
          />
        <button type='submit'>Submit</button>
        </form>
        <PersonalInfo personalInfos={personalInfos} />
      </div>
    );
  }
}
export default App;
