import React, { Component } from "react";
import '../styles/PersonalInfo.css';

class PersonalInfo extends Component {
  constructor(props) {
    super(props);

    this.state = {
      firstName: 'first name',
      lastName: 'last name',
      email: '',
      phoneNumber: ''
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      [event.target.name] : event.target.value
    })
  }

  handleSubmit(event){
    const { firstName, lastName, email, phoneNumber } = this.state;
    event.preventDefault();
    console.log("Name: " + firstName);
    console.log("Last name: " + lastName);
    console.log("email: " + email);
    console.log("Phone Number: " + phoneNumber);
  }

  render(){
    return(
      <div className="formContainer">
        <div className="title">Personal Information</div>
        <div className="personal-form">
          <form className="form-section">
            <input type="text" name="firstName" value={this.state.firstName} placeholder="First name" onChange={this.handleChange} />
            <input type="text" name="lastName" value={this.state.lastName} placeholder="Last name" onChange={this.handleChange} />
            <input type="text" name="email" value={this.state.email} placeholder="Email address" onChange={this.handleChange} />
            <input type="text" name="phoneNumber" value={this.state.phoneNumber} placeholder="Phone number" onChange={this.handleChange} />
            <button className="submit-button" onClick={this.handleSubmit}>Submit</button>
          </form> 
        </div>

        <div className="cv-section">
          <div className="cv-header">
            <div className="cv-header-left">
              <div className="first-name">{this.state.firstName}</div>
              <div className="last-name">{this.state.lastName}</div>
            </div>
            <div className="cv-header-right">
              <div className="email">{this.state.email}</div>
              <div className="phone-number">{this.state.phoneNumber}</div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default PersonalInfo;
