					<label>
						<p>First Name:</p>
						<input
							type='text'
							placeholder='First Name'
							name='firstName'
							onChange={handleChange}
							value={firstName}
							required
						/>
					</label>

					<label>
						<p>Last Name:</p>
						<input
							type='text'
							placeholder='Last Name'
							name='lastName'
							onChange={handleChange}
							value={lastName}
							required
						/>
					</label>

					<label>
						<p>Email:</p>
						<input
							type='email'
							placeholder='example@gmail.com'
							name='email'
							onChange={handleChange}
							value={email}
							required
						/>
					</label>
					<label>
						<p>Phone Number:</p>
						<input
							type='tel'
							name='phone'
							placeholder='123-456-7890'
							onChange={handleChange}
							value={phone}
							required
						/>
					</label>
					<button className='formBtn generalBtn' type='submit'>
						Save
					</button>
				</form>
			</section>
  );
}; 

export default GeneralSection;
